import ReactDOM from 'react-dom';
export let mycontextOne = React.createContext();
console.log(mycontextOne);
class A extends React.Component{
  constructor(props) {
      super(props);
      this.state = {
      	number: 1
      }
      this.setNumber = this.setNumber.bind(this);
  }
  setNumber(){
  	let number = this.state.number + 1;
  	this.setState({
  		'number':number
  	});
  }
  shouldComponentUpdate(prop,state,context){
  	console.log(prop,state,context);
  	if (state.number >=5) {
  		return false;
  	}else{
  		return true;
  	}
  }
  render () {
    return (
      <React.Fragment>
        <button onClick={this.setNumber}>设置</button>
       <mycontextOne.Provider value={this.state.number}>
        <B	/>
       </mycontextOne.Provider>

      </React.Fragment>
      );
  }
}

